    public static function prettyUrls($string)
    {
        $string = str_replace(' ', '-', $string); // Replaces all spaces with hyphens.
        return preg_replace('/[^A-Za-z0-9\-]/', '', strtolower($string)); // Removes special chars.
    }

    public static function getCanonicalDate($date, $flag = true)
    {
        return $flag ? date('d-m-Y', strtotime($date)) : date('Y-m-d', strtotime($date));
    }

    public static function getCanonicalDateTime($date, $flag = true)
    {
        return $flag ? date('d-m-Y g:i A', strtotime($date)) : date('Y-m-d H:i:s', strtotime($date));
    }

    public static function limitText(?string $string, int $limit = 120, string $suffix = "...")
    {
        if (empty($string)) return '';
        else {
            return Str::limit(html_entity_decode(strip_tags($string)), $limit, $suffix);
        }
    }

   public static function limitTextReadMore(?string $string, int $limit = 120, string $suffix = "...", bool $isTruncated = true)
    {
        if (empty($string)) return '';
        // If $isTruncated is true, limit the string; otherwise, return the full string
        if ($isTruncated) {
            return Str::limit(html_entity_decode(strip_tags($string)), $limit, $suffix);
        } else {
            return html_entity_decode(strip_tags($string));
        }
    }

