    public static function formatCarbonDate($date, string $format = 'd-M-Y'): string
    {
        $carbonDate = is_string($date) ? Carbon::parse($date) : $date;
        if ($carbonDate) {
            return $carbonDate->format($format ?? '');
        } else {
            return ' - ';
        }
    }
