    public static function generateFileName($file)
    {
        $fileExt = $file->getClientOriginalExtension();
        return  Str::uuid() . '.' . $fileExt;
    }

    public static function generateLongUniqueId($length = 16)
    {
        $uuid = Str::uuid();
        // Extract the numeric part of the UUID and format it as a 16-digit string
        $uniqueId = str_replace('-', '', $uuid->getHex());
        // Ensure the receipt ID is exactly 16 digits by truncating or padding as needed
        $uniqueId = str_pad(substr($uniqueId, 0, 16), 16, '0', STR_PAD_RIGHT);
        return $uniqueId;
    }

