	public static function imageForPDF($imageName, $directory)
    {
        if(empty($directory)) return;
        // Generate the full path to the image
        $imagePath = storage_path('app/public/' . $directory . '/' . $imageName);

        if (!file_exists($imagePath)) {
            return null;
        }

        $imageData = base64_encode(file_get_contents($imagePath));
        $extension = pathinfo($imagePath, PATHINFO_EXTENSION);
        return 'data:image/' . $extension . ';base64,' . $imageData;
    }
